DROP TABLE IF EXISTS `bl_shop_delivery`;
CREATE TABLE `bl_shop_delivery` (
  `delivery_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '物流单号',
  `delivery_no` varchar(100) DEFAULT NULL COMMENT '物流单号',
  `order_id` int(11) NOT NULL COMMENT '订单编号',
  `member_id` int(11) NOT NULL COMMENT '会员编号',
  `status` enum('succ','failed','cancel','lost','progress','timeout','ready') DEFAULT NULL COMMENT '发送状态\nready:准备发送\nprogress:发送中\nreturns:退货\nsucc:发送成功\nfailed:发送失败\ncancel:取消发送\nlost:丢失\ntimeout:发送超时',
  `type` enum('returns','delivery') DEFAULT 'delivery' COMMENT '快递类型\nreturns:返送\ndelivery:发送',
  `delivery` varchar(200) DEFAULT NULL COMMENT '快递公司',
  `ship_name` varchar(200) DEFAULT NULL COMMENT '收货人',
  `ship_area` varchar(200) DEFAULT NULL COMMENT '收货地区',
  `ship_addr` varchar(200) DEFAULT NULL COMMENT '收货地址',
  `ship_zip` varchar(20) DEFAULT NULL COMMENT '邮编',
  `ship_time` varchar(200) DEFAULT NULL COMMENT '送货时间',
  `ship_tel` varchar(20) DEFAULT NULL COMMENT '收货人电话',
  `ship_mobile` varchar(20) DEFAULT NULL COMMENT '收货人手机',
  `memo` varchar(200) DEFAULT NULL COMMENT '备注',
  `committime` int(10) DEFAULT NULL COMMENT '生成时间',
  `updateTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`delivery_id`,`order_id`,`member_id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COMMENT='物流';

-- ----------------------------
-- Records of bl_shop_delivery
-- ----------------------------

