  `product_name` varchar(200) DEFAULT NULL COMMENT '商品名称',
  `price` decimal(10,3) DEFAULT NULL COMMENT '商品价格',
  `number` int(11) DEFAULT NULL COMMENT '数量',
  `commitTime` int(10) DEFAULT NULL COMMENT '创建时间',
