-- Table structure for `bl_shop_invoice`
-- ----------------------------
DROP TABLE IF EXISTS `bl_shop_invoice`;
CREATE TABLE `bl_shop_invoice` (
  `invoice_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '标识',
  `order_id` int(11) DEFAULT NULL COMMENT '订单标识',
  `member_id` int(11) DEFAULT NULL COMMENT '会员标识',
  `invoice_code` int(11) DEFAULT NULL COMMENT '发票号',
  `invoice_state` enum('1','2','3') DEFAULT NULL COMMENT '发票状态\n1:待审核-waitaudit\n2:有效-effective\n3:无效-invalid',
  `invoice_party` varchar(50) DEFAULT NULL COMMENT '开票方',
  `price` decimal(20,3) DEFAULT NULL COMMENT '开票金额',
  `type` enum('1','2') DEFAULT NULL COMMENT '发票类型\n1:普通发票-cominvoice\n2:增值税发票-taxinvoice',
  `type1_header` enum('1','2') DEFAULT NULL COMMENT '发票抬头\n1:个人-owners\n 2:单位-enterprises',
  `type1_name` varchar(400) DEFAULT NULL COMMENT '个人单位名称',
  `company` varchar(400) DEFAULT NULL COMMENT '单位名称',
  `content` varchar(200) DEFAULT NULL COMMENT '发票内容',
  `taxpayer` varchar(400) DEFAULT NULL COMMENT '纳税人识别号',
  `reg_address` varchar(400) DEFAULT NULL COMMENT '注册地址',
  `reg_tel` varchar(100) DEFAULT NULL COMMENT '注册电话',
  `bank` varchar(400) DEFAULT NULL COMMENT '开户银行',
  `bank_account` varchar(300) DEFAULT NULL COMMENT '银行账号',
  `memo` varchar(500) DEFAULT NULL COMMENT '备注',
  `fee` decimal(5,3) DEFAULT NULL COMMENT '发票费用',
  `commitTime` int(11) DEFAULT NULL COMMENT '创建时间',
  `updateTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`invoice_id`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COMMENT='订单发票';

