-- Table structure for `bl_shop_order`
-- ----------------------------
DROP TABLE IF EXISTS `bl_shop_order`;
CREATE TABLE `bl_shop_order` (
  `order_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '标识',
  `order_code` varchar(400) DEFAULT NULL COMMENT '订单编号',
  `member_id` mediumint(9) DEFAULT '0' COMMENT '易乐会员的编号',
  `statue` enum('active','dead','confirm','audit','finish') DEFAULT NULL COMMENT '订单状态\nactive:有效\nconfirm:确认\nfinish:完成\ndead:无效',
  `pay_status` enum('0','1','2','3','4','5','6','7','8') DEFAULT NULL COMMENT '支付状态\n0:准备支付-ready\n1:支付成功-succ\n2:支付中-progress\n3:支付失败-failed\n4:取消支付-cancel\n5:退款-returned\n6:支付错误-error\n7:非法支付-invalid\n8:支付超时-timeout',
  `ship_status` enum('0','1','2','3','5','4') DEFAULT NULL COMMENT '配送状态\n0:未发货-undispatch\n1:已发货-dispatch\n2:部分发货-dispatchpart\n3:部分退货-returnedpart\n4:已退货-returned\n5:已签收-signed',
  `total_amount` decimal(20,3) DEFAULT '0.000' COMMENT '订单总额',
  `final_amount` decimal(20,3) DEFAULT '0.000' COMMENT '成交总价格',
  `total_jifen` int(11) DEFAULT '0' COMMENT '订单总积分',
  `final_jifen` int(11) DEFAULT '0' COMMENT '成交总积分',
  `cost_item` decimal(20,3) DEFAULT '0.000' COMMENT '商品总价格',
  `cost_other` decimal(20,3) DEFAULT '0.000' COMMENT '其他费用\n如货运费，税费，保价等。',
  `pmt_amount` decimal(20,3) DEFAULT '0.000' COMMENT '促销优惠总金额',
  `pay_type` tinyint(4) DEFAULT NULL COMMENT '支付方式标识',
  `country` smallint(5) DEFAULT NULL COMMENT '国家\n参考region表的region_id字段',
  `province` smallint(5) DEFAULT NULL COMMENT '省\n参考region表的region_id字段',
  `city` smallint(5) DEFAULT NULL COMMENT '市\n参考region表的region_id字段',
  `district` smallint(5) DEFAULT NULL COMMENT '区\n参考region表的region_id字段',
  `ship_addr` varchar(500) DEFAULT NULL COMMENT '收货地址',
  `ship_email` varchar(200) DEFAULT NULL COMMENT '邮箱',
  `ship_name` varchar(45) DEFAULT NULL COMMENT '收货人',
  `ship_tel` varchar(45) DEFAULT NULL COMMENT '收货电话',
  `ship_mobile` varchar(45) DEFAULT NULL COMMENT '收货手机',
  `ship_time` varchar(50) DEFAULT NULL COMMENT '送货时间\n\n如:\n     任何日期任何时间段\n     仅工作日上午\n     仅工作日下午\n     仅休息日下午\n     2011-05-01 下午',
  `ship_type` tinyint(4) DEFAULT NULL COMMENT '配送方式标识\n',
  `ship_sign_building` varchar(120) DEFAULT NULL COMMENT '标志建筑',
  `invoice_id` int(11) DEFAULT NULL COMMENT '订单发票标识',
  `ship_zipcode` varchar(60) DEFAULT NULL COMMENT '邮政编码',
  `memo` longtext COMMENT '订单附言',
  `ordertime` int(10) DEFAULT NULL COMMENT '订购时间',
  `commitTime` int(11) DEFAULT NULL COMMENT '创建时间',
  `updateTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`order_id`),
  UNIQUE KEY `id_UNIQUE` (`order_id`),
  KEY `fk_ey_order_ey_user` (`member_id`)
) ENGINE=MyISAM AUTO_INCREMENT=627 DEFAULT CHARSET=utf8 COMMENT='会员的订单';

